angular.module('smartMeeting')
.controller('ProjectCtrl', [
  '$scope',
  '$location',
  'project',
  'users',
  'meetings',
  'Auth',
  'projects',
  '$timeout',
  'meetingStatuses',
  function($scope, $location, project, users, meetings, Auth, projects, $timeout, meetingStatuses){
    $scope.meetingStatuses = meetingStatuses;
    $scope.project = project;
    setDefaults();

    function setDefaults(){
      $scope.title = '';
      $scope.attendees = [
        { username:project.owner.username } //include owner as default member of new meeting
      ];
      $scope.date = '';
      $scope.location = '';
      $scope.description = '';
      $scope.duration = 60;
    }

    $scope.createMeeting = function(){
      if(!validateMeeting()){
        return;
      }

      meetings.create({
        project_id: $scope.project.id,
        title: $scope.title,
        attendees: $scope.attendees,
        duration: $scope.duration,
        location: $scope.location,
        description: $scope.description,
        date: $scope.datetime.toISOString()
      }).success(function(meeting){
        $scope.project.meetings.push(meeting);
      });
      setDefaults(); //cleans fields
    };

    function showError(message){
      $scope.formError = false;
      $scope.doFade = false;
      $scope.formError = true;
      $scope.errorMessage = message;
      $timeout(function(){
        $scope.doFade = true;
      }, 2500);
    }

    function validateMeeting(){
      if(!$scope.title || $scope.title === ''){
        showError('Invalid Meeting Title.');
        return false;
      }
      else if(!$scope.attendees || $scope.attendees === ''){
        showError('Must have at least one attendee.');
        return false;
      }
      else if(!$scope.datetime || $scope.datetime === '') {
        showError('Must specify a date and time.');
      }
      else if(!$scope.duration || $scope.duration === ''){
        showError('Meeting must have a duration.');
        return false;
      }
      else{
        return true;
      }
    }

    $scope.gcalLogin = function() {
      window.location.href = "/auth/google_oauth2" + "?state=" + location.pathname;
    };

    $scope.autocompleteUsers = function(query){
      return users.search(query);
    };

    $scope.joinProject = function(project){
      projects.join(project);
    };

    $scope.leaveProject = function(project){
      projects.leave(project);
    };

    $scope.goToMeeting = function(meeting) {
      var meetingUrl = "/a/meetings/" + meeting.id;
      $location.url(meetingUrl);
    };

    $scope.deleteMeeting = function(meeting){
      meetings.delete(meeting.id).success(function(){
        $scope.project.meetings = _.reject($scope.project.meetings, function(thismeeting){ return thismeeting.id == meeting.id; });
      });
    };
  }
]);
angular.module('smartMeeting')
.factory('projects', [
    '$http',
    '$location',
    function($http, $location){
        var o = {
            projects: [],
            memberprojects: [],
        };
        o.setCurrentUser = function(current_user){
            o.current_user = current_user;
        };
        o.getAll = function(){
            return $http.get('/projects.json').success(function(data){
                var projects = [];
                _.each(data, function(project){
                    project = o.addIsMemberToProjectHash(project);
                    projects.push(project);
                });
                angular.copy(projects, o.projects);
            });
        };
        o.create = function(project){
            return $http.post('/projects.json', project).success(function(data){
                data.isMember = true;
                o.projects.push(data);
            });
        };
        o.get = function(id){
            return $http.get('/projects/' + id + '.json').then(function(res){
                project = o.addIsMemberToProjectHash(res.data);
                return project;
            });
        };

        o.addIsMemberToProjectHash = function(project){
            var project_user_ids = _.pluck(project.users, 'id');
            project.isMember = _.contains(project_user_ids, o.current_user.id) ? true : false;
            return project;
        };

        o.join = function(project){
            return $http.put('/projects/' + project.id + '/join.json').success(function(data){
                project.users.push(o.current_user);
                project.isMember = true;
            });
        };

        o.leave = function(project){
            return $http.put('/projects/' + project.id + '/leave.json').success(function(res){
                project.isMember = false;
                project.users = _.reject(project.users, function(user){ return user.id == o.current_user.id; });
            });
        };

        o.delete = function(id){
            return $http.delete('/projects/' + id + '.json');
        };

        o.redirectToMeeting = function(slug){
            return $http.get('/projects/' + slug + '/latest_meeting.json').then(function(res){
                $location.path("/meeting/" + res.data.access_code);
            });
        };

        return o;
    }
]);
angular.module('smartMeeting')
.controller('ProjectsCtrl', [
  '$scope',
  'projects',
  'users',
  '$timeout',
  function($scope, projects, users, $timeout){
    $scope.projects = projects.projects;
    $scope.memberprojects = projects.memberprojects;
    $scope.members = [{ username:projects.current_user.username }];

    $scope.createProject = function(){
      if(!validateProject()){
        return;
      }
      projects.create({
        title: $scope.title,
        members: $scope.members
      });
      $scope.title = '';
      $scope.members = [{ username:projects.current_user.username }];
    };

    $scope.autocompleteUsers = function(query){
      return users.search(query);
    };

    $scope.joinProject = function(project){
      projects.join(project);
    };

    $scope.leaveProject = function(project){
      projects.leave(project);
    };

    $scope.deleteProject = function(project){
      projects.delete(project.id).success(function(){
        $scope.projects = _.reject($scope.projects, function(thisproject){ return thisproject.id == project.id; });
      });
    };

    function showError(message){
      $scope.formError = false;
      $scope.doFade = false;
      $scope.formError = true;
      $scope.errorMessage = message;
      $timeout(function(){
        $scope.doFade = true;
      }, 2500);
    }

    function validateProject(){
      if(!$scope.title || $scope.title === ''){
        showError('Invalid Project Title');
        return false;
      }
      else if(!$scope.members || $scope.members === ''){
        showError('Must have at least one member');
        return false;
      }
      else{
        return true;
      }
    }
  }
]);
angular.module('smartMeeting')
.factory('users', [
  '$http',
  '$q',
  function($http, $q){
    var o = {
        users: []
      };
    o.search = function(query){
        return $http.get('/users/search.json' + '?query=' + query).success(function(data){
            angular.copy(data, o.users);
        });
    };
    o.searchByProject = function(query, project_id){
        return $http.get('/users/search.json' + '?query=' + query + '&project_id=' + project_id).success(function(data){
            angular.copy(data, o.users);
        });
    };

    o.getCalendars = function(){
      var defer;
      defer = $q.defer();
      $http.get('users/get_calendar_events.json').success(function(data){
        defer.resolve(data);
      });
      return defer.promise;
    };


    return o;
  }
]);
